<html>
  <body>
    <script src="https://web.janusxr.org/1.5.53/janusweb.js"></script>
    <janus-viewer>
      <assets>
        <assetscript>
          room.registerElement('clickable-cube', {
            create() {
              this.originalPosition = this.object.position.clone();
              this.originalRotation = this.object.rotation.clone();
              this.isAnimating = false;

              this.object.addEventListener('click', () => {
                if (!this.isAnimating) {
                  this.animate();
                }
              });
            },
            animate() {
              this.isAnimating = true;

              // Jump Animation
              const jumpHeight = 2;
              const jumpDuration = 0.2; // in seconds

              // Spin Animation
              const spinDuration = 0.2; // in seconds
              const spinAngle = Math.PI * 2; // one full rotation

              // Using Tween for animation
              new TWEEN.Tween(this.object.position)
                .to({ y: this.originalPosition.y + jumpHeight }, jumpDuration * 1000)
                .easing(TWEEN.Easing.Quadratic.Out)
                .onComplete(() => {
                  new TWEEN.Tween(this.object.position)
                    .to({ y: this.originalPosition.y }, jumpDuration * 1000)
                    .easing(TWEEN.Easing.Quadratic.In)
                    .start();
                })
                .start();

              new TWEEN.Tween(this.object.rotation)
                .to({ y: this.originalRotation.y + spinAngle }, spinDuration * 1000)
                .easing(TWEEN.Easing.Linear.None)
                .onComplete(() => {
                  this.object.rotation.copy(this.originalRotation);
                  this.isAnimating = false;
                })
                .start();
            },
            update(dt) {
              TWEEN.update();
            }
          });
        </assetscript>
      </assets>
